// App.js

import React, { useState, useEffect } from 'react';
import KanbanBoard from './KanbanBoard';
import './App.css';

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupingOption, setGroupingOption] = useState('status');
  const [sortingOption, setSortingOption] = useState('priority');

  // Fetch data from the API using useEffect
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setTickets(data.tickets);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Define event handlers for grouping and sorting options
  const handleGroupingChange = (option) => {
    setGroupingOption(option);
  };

  const handleSortingChange = (option) => {
    setSortingOption(option);
  };

  return (
    <div className="app">
      <div className="header">
        <label className="header-label"> Display </label>
        <div className="display">
          <label>Grouping: </label>
          <select
            className="select-grouping"
            onChange={(e) => handleGroupingChange(e.target.value)}
          >
            <option value="status">Status</option>
            <option value="user">User</option>
            <option value="priority">Priority</option>
          </select>
        </div>
        <div className="display">
          <label>Ordering: </label>
          <select
            className="select-sorting"
            onChange={(e) => handleSortingChange(e.target.value)}
          >
            <option value="priority">Priority</option>
            <option value="title">Title</option>
          </select>
        </div>
      </div>
      <div className="horizontal-elements">
        <div className="horizontal-element">Element 1</div>
        <div className="horizontal-element">Element 2</div>
        <div className="horizontal-element">Element 3</div>
        <div className="horizontal-element">Element 4</div>
      </div>
      <KanbanBoard
        tickets={tickets}
        groupingOption={groupingOption}
        sortingOption={sortingOption}
      />
    </div>
  );




}

export default App;
