import React, { useState, useEffect } from 'react';
import KanbanBoard from './KanbanBoard';
import './App.css';

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupingOption, setGroupingOption] = useState('status');
  const [sortingOption, setSortingOption] = useState('priority');
  const [isModalOpen, setIsModalOpen] = useState(false);
  // Define the missing state variable 'areListsVisible' and its setter function
  const [areListsVisible, setAreListsVisible] = useState(false);

  // Fetch data from the API using useEffect
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setTickets(data.tickets);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Define event handlers for grouping and sorting options
  const handleGroupingChange = (option) => {
    setGroupingOption(option);
  };

  const handleSortingChange = (option) => {
    setSortingOption(option);
  };

  // Define the missing 'toggleListsVisibility' function
  const toggleListsVisibility = () => {
    setAreListsVisible(!areListsVisible);
  };

// Inside your App component's render method
return (
<div className="app">
  <div className="display">
    <button onClick={toggleListsVisibility} className="display-button">Display</button>
  </div>
  <div className="header">
    <label>Grouping: </label>
    <select onChange={(e) => handleGroupingChange(e.target.value)}>
      <option value="status">Status</option>
      <option value="user">User</option>
      <option value="priority">Priority</option>
    </select>
    <label>Ordering: </label>
    <select onChange={(e) => handleSortingChange(e.target.value)}>
      <option value="priority">Priority</option>
      <option value="title">Title</option>
    </select>
  </div>
  <KanbanBoard tickets={tickets} groupingOption={groupingOption} sortingOption={sortingOption} />
</div>

);

}

export default App;
