// App.js

import React, { useState, useEffect } from 'react';
import KanbanBoard from './KanbanBoard';

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupingOption, setGroupingOption] = useState('status');
  const [sortingOption, setSortingOption] = useState('priority');

  // Fetch data from the API using useEffect
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setTickets(data.tickets);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Define event handlers for grouping and sorting options
  const handleGroupingChange = (option) => {
    setGroupingOption(option);
  };

  const handleSortingChange = (option) => {
    setSortingOption(option);
  };

  return (
    <div className="app">
      <h1>Kanban Board</h1>
      {/* User interface for selecting grouping and sorting options */}
      <div className="display">
        <label>Display: </label>
        <select>
          <option value="grouping">Grouping</option>
          <option value="sorting">Sorting</option>
        </select>
        {groupingOption === 'grouping' && (
          <div className="nested-dropdown">
            <label>Grouping: </label>
            <select onChange={(e) => setGroupingOption(e.target.value)}>
              <option value="status">Status</option>
              <option value="user">User</option>
              <option value="priority">Priority</option>
            </select>
          </div>
        )}
        {sortingOption === 'sorting' && (
          <div className="nested-dropdown">
            <label>Sorting: </label>
            <select onChange={(e) => setSortingOption(e.target.value)}>
              <option value="priority">Priority</option>
              <option value="title">Title</option>
            </select>
          </div>
        )}
      </div>

      <KanbanBoard
        tickets={tickets}
        groupingOption={groupingOption}
        sortingOption={sortingOption}
      />
    </div>
  );
}

export default App;
