// KanbanBoard.js

import React from 'react';

function KanbanBoard({ tickets, groupingOption, sortingOption }) {
  // Function to group tickets based on the selected option
  const groupTickets = () => {
    if (groupingOption === 'status') {
      // Group tickets by status
      const grouped = {};
      tickets.forEach((ticket) => {
        if (!grouped[ticket.status]) {
          grouped[ticket.status] = [];
        }
        grouped[ticket.status].push(ticket);
      });
      return grouped;
    } else if (groupingOption === 'user') {
      // Group tickets by user
      const grouped = {};
      tickets.forEach((ticket) => {
        if (!grouped[ticket.userId]) {
          grouped[ticket.userId] = [];
        }
        grouped[ticket.userId].push(ticket);
      });
      return grouped;
    } else if (groupingOption === 'priority') {
      // Group tickets by priority level
      const grouped = {
        4: [], // Urgent
        3: [], // High
        2: [], // Medium
        1: [], // Low
        0: [], // No priority
      };
      tickets.forEach((ticket) => {
        grouped[ticket.priority].push(ticket);
      });
      return grouped;
    }
  };

  // Function to sort tickets based on the selected option
  const sortTickets = () => {
    if (sortingOption === 'priority') {
      // Sort tickets by priority in descending order
      return [...tickets].sort((a, b) => b.priority - a.priority);
    } else if (sortingOption === 'title') {
      // Sort tickets by title in ascending order
      return [...tickets].sort((a, b) => a.title.localeCompare(b.title));
    }
  };

  // Call the grouping and sorting functions to get the grouped and sorted tickets
  const groupedTickets = groupTickets();
  const sortedTickets = sortTickets();

  return (
    <div className="kanban-board">
      {/* Render the grouped and sorted tickets here */}
    </div>
  );
}

export default KanbanBoard;
