import React, { useState, useEffect } from 'react';
import KanbanBoard from './KanbanBoard';
import './App.css';
import sliderImage from './slider.png';

function App() {
  // Initialize user settings from Local Storage or provide default values
  const savedUserSettings = JSON.parse(localStorage.getItem('userSettings')) || {
    groupingOption: 'status',
    sortingOption: 'priority',
    backgroundColor: '#ffffff', // Default background color
  };

  const [tickets, setTickets] = useState([]);
  const [groupingOption, setGroupingOption] = useState(savedUserSettings.groupingOption);
  const [sortingOption, setSortingOption] = useState(savedUserSettings.sortingOption);
  const [backgroundColor, setBackgroundColor] = useState(savedUserSettings.backgroundColor);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isOverlayVisible, setIsOverlayVisible] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setTickets(data.tickets);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleGroupingChange = (option) => {
    setGroupingOption(option);
  };

  const handleSortingChange = (option) => {
    setSortingOption(option);
  };

  const handleBackgroundColorChange = (color) => {
    setBackgroundColor(color);
  };

  useEffect(() => {
    // Update user settings in Local Storage whenever they change
    const userSettings = {
      groupingOption,
      sortingOption,
      backgroundColor,
    };
    localStorage.setItem('userSettings', JSON.stringify(userSettings));
  }, [groupingOption, sortingOption, backgroundColor]);

  const toggleOverlayVisibility = () => {
    setIsOverlayVisible(!isOverlayVisible);
  };

  return (
    <div className="app" style={{ backgroundColor: backgroundColor }}>
      <nav className="navbar">
        <div className="overlay-button">
          <button className="display-button" onClick={toggleOverlayVisibility}>
            <img className="small-image rotated-image" src={sliderImage} alt="Application" /> Display
          </button>
          <div className="dropdown-content" style={{ display: isOverlayVisible ? 'block' : 'none' }}>
            <div className="grp">
              <label className="grp-tit">Grouping:</label>
              <div className="grp-se">
                <select onChange={(e) => handleGroupingChange(e.target.value)} value={groupingOption} className="grp-on">
                  <option value="status">Status</option>
                  <option value="user">User</option>
                  <option value="priority">Priority</option>
                </select>
              </div>
            </div>
            <div className="sor">
              <label className="sor-tit">Sorting:</label>
              <div className="sor-se">
                <select onChange={(e) => handleSortingChange(e.target.value)} value={sortingOption} className="sor-on">
                  <option value="priority">Priority</option>
                  <option value="title">Title</option>
                </select>
              </div>
            </div>
            <div className="bg-color">
              <label className="bg-color-tit">Background Color:</label>
              <input
                type="color"
                onChange={(e) => handleBackgroundColorChange(e.target.value)}
                value={backgroundColor}
              />
            </div>
          </div>
        </div>
      </nav>
      <KanbanBoard tickets={tickets} groupingOption={groupingOption} sortingOption={sortingOption} />
    </div>
  );
}

export default App;
