import React from 'react';

function KanbanBoard({ tickets, groupingOption, sortingOption }) {
  // Define functions for grouping and sorting tickets based on the options
  const groupTickets = (tickets, groupingOption) => {
    if (groupingOption === 'status') {
      // Group by status
      const grouped = {};
      tickets.forEach((ticket) => {
        if (!grouped[ticket.status]) {
          grouped[ticket.status] = [];
        }
        grouped[ticket.status].push(ticket);
      });
      return grouped;
    } else if (groupingOption === 'user') {
      // Group by user
      const grouped = {};
      tickets.forEach((ticket) => {
        if (!grouped[ticket.userId]) {
          grouped[ticket.userId] = [];
        }
        grouped[ticket.userId].push(ticket);
      });
      return grouped;
    } else if (groupingOption === 'priority') {
      // Group by priority
      const grouped = {};
      tickets.forEach((ticket) => {
        if (!grouped[ticket.priority]) {
          grouped[ticket.priority] = [];
        }
        grouped[ticket.priority].push(ticket);
      });
      return grouped;
    }
  };

  const sortTickets = (tickets, sortingOption) => {
    if (sortingOption === 'priority') {
      // Sort by priority in descending order
      return [...tickets].sort((a, b) => b.priority - a.priority);
    } else if (sortingOption === 'title') {
      // Sort by title in ascending order
      return [...tickets].sort((a, b) => a.title.localeCompare(b.title));
    }
  };

  // Group and sort the tickets
  const groupedTickets = groupTickets(tickets, groupingOption);
  const sortedTickets = sortTickets(tickets, sortingOption);

  return (
    <div className="kanban-board">
      {Object.keys(groupedTickets).map((group, index) => (
        <div key={index} className="group">
        <h2 className="group-header">
  {group} {groupedTickets[group].length}
  <button className="add-button">+ ...</button>
</h2>
          {sortedTickets.map((ticket) => {
            if (groupedTickets[group].includes(ticket)) {
              return (
                <div key={ticket.id} className="ticket">
                  <div className="ticket-header">
                    <div className="ticket-id"> {ticket.id}</div>
                    <div className="ticket-tag">Feature Request</div>
                  </div>
                  <div className="ticket-title">{ticket.title}</div>
                  <div className="ticket-info">
                    <div>Status: {ticket.status}</div>
                    <div>Priority: {ticket.priority}</div>
                    <div>User: {ticket.userId}</div>
                  </div>
                </div>
              );
            }
            return null;
          })}
        </div>
      ))}
    </div>
  );
}

export default KanbanBoard;
